import React, { useState } from 'react'
import './header.css'
import Logo from '../../assets/logo.png'
import Bars from '../../assets/bars.png'
import { Link } from 'react-scroll';

const Header = () => {
  const mobile = window.innerWidth <= 768 ? true : false;
  //by giving false menu will not open by default
  const [menuOpened, setMenuOpened] = useState(false)
  return (
    <div className='header'>
      <img src={Logo} alt='' className='logo' />
      {(menuOpened === false && mobile === true) ? (
        <div
          style={{
            backgroundColor: 'var(--appColor)',
            padding: '0.5rem',
            borderRadius: '5px'
          }}
          onClick={() => setMenuOpened(true)}
        ><img src={Bars} alt='' style={{ width: '1.5rem', height: '1.5rem' }} /></div>
      ) : <ul className='header-menu'>
        <li >
          <Link
            onClick={() => setMenuOpened(false)}
            // here activeClass is prop which means home is default class which is active
            activeClass='active'
            to='header'
            span={true}
            smooth={true}>Home</Link>
        </li>
        <li onClick={() => setMenuOpened(false)}><Link
          onClick={() => setMenuOpened(false)}
          to='Programs'
          span={true}
          smooth={true}
        >Programs</Link></li>
        <li onClick={() => setMenuOpened(false)}><Link
          onClick={() => setMenuOpened(false)}
          to='plans'
          span={true}
          smooth={true}
        >Plans</Link></li>
        <li ><Link
        onClick={() => setMenuOpened(false)} 
        to='reasons'
          span={true}
          smooth={true}
        >Why us</Link></li>
        <li><Link
          onClick={() => setMenuOpened(false)}
          to='Testimonials'
          span={true}
          smooth={true}
        >Testimonials</Link></li>
      </ul>
      }

    </div >
  )
}

export default Header
